import pygame as pgimport sysimport timeimport randomfrom main import*from game import*import astroid_warsimport settingsimport ospg.init()screen_width = 800screen_height = 640black = (0,0,0)white = (255,255,255)red = (176,0,0)green = (0,150,0)blue = (36, 115, 148)grey = (110, 110, 110)dark_red = (149,0,0)dark_blue = (26, 101, 134)dark_grey = (100,100, 100)dark_green = (0, 110, 0)screen = pg.display.set_mode((800,640))gameDisplay = pg.display.set_mode((screen_width, screen_height))pg.display.set_caption('CivX')clock = pg.time.Clock()pg.mixer.init()pg.mixer.music.load("Infinity Awaits Us.mp3")pg.mixer.music.play()def menu_animation():    n = 1    while n != 24:        for event in pg.event.get():            if event.type == pg.QUIT:                pg.quit                quit()        frame = pg.image.load("Main Menu-{0} (dragged).png".format(n))        screen.blit(frame, ((0, 0)))        clock.tick(4)        pg.display.update()        n += 1    def text_objects(text, font):        textSurface = font.render(text, True, settings.WHITE)    return textSurface, textSurface.get_rect()def title_screen(text):    largeText = pg.font.Font('Megrim.ttf', 115)    TextSurf, textRect = text_objects(text, largeText)    textRect.center = ((80),(200))    gameDisplay.blit(TextSurf, textRect)    pg.display.update()    time.sleep(24)def selected_game():    runFirst = True    while runFirst:        for event in pg.event.get():            if event.type == pg.QUIT:                pg.quit()                sys.exit()                        screen.fill(black)        pg.display.update()        #def button(msg, x, y, w, h, ic, ac):        button ("Return", 22, 22, 130, 40, settings.GREY, settings.DARK_GREY, "Return")                button ("Dheeraj's Game", 100, 450, 155, 40, blue, dark_blue, "Dheeraj's Game")        button ("Ryan's Game", 350, 450, 150, 40, red, dark_red, "Ryan's Game")        button ("Harsimar's Game", 600, 450, 160, 40, green, dark_green, "Harsimar's Game")                        pg.display.update()def select_HTP():        runFirst = True    while runFirst:        for event in pg.event.get():            if event.type == pg.QUIT:                pg.quit()                sys.exit()                        screen.fill(black)        #def button(msg, x, y, w, h, ic, ac):        button ("Return", 22, 22, 130, 40, settings.GREY, settings.DARK_GREY, "Return")        button ("Dheeraj", 100, 450, 130, 40, blue, dark_blue, "Dheeraj")        button ("Ryan", 350, 450, 130, 40, red, dark_red, "Ryan")        button ("Harsimar", 600, 450, 130, 40, green, dark_green, "Harsimar")                pg.display.update()def D():    runFirst = True    while runFirst:        for event in pg.event.get():            if event.type == pg.QUIT:                pg.quit()                sys.exit()                        screen.fill(black)        HTP = pg.image.load("HTP3.png")        screen.blit(HTP, ((0,0)))        button ("Return", 22, 22, 130, 40, settings.GREY, settings.DARK_GREY, "Return")                pg.display.update()        def R():    runFirst = True    while runFirst:        for event in pg.event.get():            if event.type == pg.QUIT:                pg.quit()                sys.exit()                        screen.fill(black)        HTP = pg.image.load("HTP2.png")        screen.blit(HTP, ((0,0)))        button ("Return", 22, 22, 130, 40, settings.GREY, settings.DARK_GREY, "Return")        pg.display.update()def H():    runFirst = True    while runFirst:        for event in pg.event.get():            if event.type == pg.QUIT:                pg.quit()                sys.exit()                        screen.fill(black)        HTP = pg.image.load("HTP.png")        screen.blit(HTP, ((0,0)))        button ("Return", 22, 22, 130, 40, settings.GREY, settings.DARK_GREY, "Return")        pg.display.update()        def button(msg, x, y, w, h, ic, ac,action=None):    mouse = pg.mouse.get_pos()    click = pg.mouse.get_pressed()    #print(click)        #print(mouse)    if x + w > mouse [0] > x and y + h > mouse [1] > y:        pg.draw.rect(gameDisplay, ac, (x,y,w,h))        if click [0]==1 and action != None:            #print("Left Key Pressed")            if action == "Play":                selected_game()            if action == "How To Play":                select_HTP()            if action == "Return":                game_menu()            if action == "Dheeraj":                D()            if action == "Ryan":                R()            if action == "Harsimar":                H()                        if action == "Ryan's Game":                game()            if action == "Dheeraj's Game":                astroid_wars.gameloop()                                        if action == "Harsimar's Game":                main()                                                           elif action == "Quit":                pg.quit()                sys.exit()                    else:        pg.draw.rect(gameDisplay, ic, (x,y,w,h))            smallText = pg.font.Font('Megrim.ttf', 20)    TextSurf, textRect = text_objects(msg, smallText)    textRect.center = ((x+(w/2),(y+(h/2))))    gameDisplay.blit(TextSurf, textRect)def game_menu():    menu = pg.image.load("Main Menu-23 (dragged).png")    screen.blit(menu, ((0,0)))    pg.display.update()    intro = True    while intro:        for event in pg.event.get():            if event.type == pg.QUIT:                pg.quit()                quit()        largeText = pg.font.Font('Megrim.ttf', 65)        TextSurf, textRect = text_objects("CivX", largeText)        textRect.center = ((80),(200))        gameDisplay.blit(TextSurf, textRect)        #def button(msg, x, y, w, h, ic, ac):        button ("Start", 22, 250, 130, 40, blue, dark_blue, "Play")        button ("How To Play", 22, 320, 160, 45, grey, dark_grey, "How To Play")        button ("Quit", 22, 390, 90, 40, red, dark_red, "Quit")                pg.display.update()                clock.tick(100)        menu_animation()game_menu()